const darkButton = document.getElementById("dark");
const browser = document.querySelector(".browser");

darkButton.addEventListener("click", addDarkClass);

function addDarkClass(e) {
  if (browser.className === "browser") {
    browser.className = "browser dark";
  } else if (browser.className === "browser dark") {
    browser.className = "browser";
  }
  e.preventDefault();
}
